var link=document.querySelector(".contacts-info .button-send"),popup=document.querySelector(".modal-post"),close=popup.querySelector(".modal-close"),form=popup.querySelector("form"),username=popup.querySelector("[name=username]"),email=popup.querySelector("[name=email]"),message=popup.querySelector("[name=message]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-feedback-show"),username.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-feedback-show"),popup.classList.remove("modal-feedback-error")}),form.addEventListener("submit",function(e){username.value&&email.value&&message.value||(e.preventDefault(),popup.classList.remove("modal-feedback-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-feedback-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-feedback-show")&&(popup.classList.remove("modal-feedback-show"),popup.classList.remove("modal-feedback-error"))});